Vue.component("card",{template:'\n    <div :class="[\'card\', \'col-sm\', color, \'text-white\', \'rounded\', \'m-1\']">\n    <div class="card-body">\n      <h5 class="card-title">{{title}}</h5>\n      <div class="row text-center">\n      <div class="col-md">\n      <img class="img-fluid rounded" :src="img1" :alt="title">\n      </div>\n      <div class="col-md">\n      <img class="img-fluid rounded" :src="img2" :alt="title">\n      </div>\n      </div>\n    </div>\n  </div>\n    ',props:["title","href","color","img1","img2"]});const app=new Vue({el:"#app",data:{posibleColors:["bg-info","bg-success","bg-dark","bg-warning","bg-danger","bg-secondary","bg-primary"]},computed:{color(){return this.posibleColors[Math.floor(Math.random()*this.posibleColors.length)]}}});